package com.zjf.demo.mode.bridge;/** * @author zhaojiafeng */public interface BaseColor {    /**     * 上色     * @param shape 形状     */    void bePaint(String shape);    class White implements BaseColor {        @Override        public void bePaint(String shape) {            System.out.println("white " + shape);        }    }    class Gray implements BaseColor {        @Override        public void bePaint(String shape) {            System.out.println("Gray " + shape);        }    }    class Black implements BaseColor {        @Override        public void bePaint(String shape) {            System.out.println("Black " + shape);        }    }}