package com.zjf.demo.mode.bridge;import lombok.Data;import java.awt.*;/** * @author zhaojiafeng */@Datapublic abstract class BaseShape {    BaseColor color;    /**     * 绘画     */    abstract void draw();    public class Circle extends BaseShape {        @Override        void draw() {            color.bePaint("circle");        }    }    static class Rectangle extends BaseShape {        @Override        void draw() {            color.bePaint("rectangle");        }    }    static class Square extends BaseShape {        @Override        void draw() {            color.bePaint("square");        }    }}