package com.zjf.demo.mode.facade;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * 门面类 * * @author zhaojiafeng */public class Facade {    private static final Logger LOGGER = LoggerFactory.getLogger(Facade.class);    private Computer computer;    private Memory memory;    private Disk disk;    public Facade() {        computer = new Computer();        memory = new Memory();        disk = new Disk();    }    public void start() {        LOGGER.info("Computer start begin");        computer.start();        disk.start();        memory.start();        LOGGER.info("Computer start end");    }    public void shutDown() {        LOGGER.info("Computer shutDown begin");        computer.shutDown();        disk.shutDown();        memory.shutDown();        LOGGER.info("Computer shutDown end...");    }}