package com.zjf.demo.mode.proxy.dynamic;import java.lang.reflect.InvocationHandler;import java.lang.reflect.Method;/** * @author zhaojiafeng */public class DynamicProxyHandler implements InvocationHandler {    private Object object;    DynamicProxyHandler(final Object object) {        this.object = object;    }    @Override    public Object invoke(Object proxy, Method method, Object[] args) throws Throwable {        System.out.println("check money");        Object result = method.invoke(object, args);        System.out.println("pay money");        return result;    }}