package com.zjf.demo.commons;import org.junit.Test;import java.util.List;import static org.junit.Assert.*;public class HttpUtilsTest {    /**     * result:     * <img src="https://img-blog.csdnimg.cn/2019092715111047.png" alt=""     * <img src="https://img-blog.csdnimg.cn/2019092715111047.pngte" alt=""     * <img src="https://img-blog.csdnimg.cn/2019092715111047.pngtest" alt=""     */    @Test    public void getImgStr() {        String context = "sss" +                "<img src=\"https://img-blog.csdnimg.cn/2019092715111047.png\" alt=\"\"/>" +                "ssssss" +                "<img src=\"https://img-blog.csdnimg.cn/2019092715111047.pngte\" alt=\"\"/>" +                "sss" + "sss" +                "<img src=\"https://img-blog.csdnimg.cn/2019092715111047.pngtest\" alt=\"\"/>" + "sss";        List<String> list = HttpUtils.getImgStr(context);        list.forEach(System.out::println);    }    /**     * result:     * https://img-blog.csdnimg.cn/2019092715111047.png     */    @Test    public void getUrlFromImg() {        String context = "<img src=\"https://img-blog.csdnimg.cn/2019092715111047.png\" alt=\"\">";        String list = HttpUtils.getUrlFromImg(context);        System.out.println(list);    }}